<script>
$(document).on("click", ".btn-icon", function () {
var row_id_value = $(this).data('row-id');
if (row_id_value == 1) {
$('#dialogueStatusText').html('Deactivate');
}
else if (row_id_value == 0) {
$('#dialogueStatusText').html('Activate');
}
});
$(document).bind('drop dragover', function (e) {
e.preventDefault();
});


$(function () {
$('.bs-example-modal-md').on('show.bs.modal', function (e) {
$(this).find('.btn-ok').attr('href', $(e.relatedTarget).data('href'));
});

$('.bs-example-modal-md-delete').on('show.bs.modal', function (e) {
$(this).find('.btn-ok-delete').attr('href', $(e.relatedTarget).data('href'));
});
});

$(document).ready(function () {
$('#searchBtn').click(function (e) {
var searchWOrd = '';
searchWOrd = $("#dictionary_word").val();
if (searchWOrd == '' || searchWOrd == null) {
$("#dictionary_word").css("border", "1px solid #F97F73");
$("#dictionary_word").focus();
} else {

$(document).ajaxStart(function () {
$("#dictionary_loader").show();
}).ajaxStop(function () {
$("#dictionary_loader").hide();
});

$.ajax({
type: "POST",
url: "https://diyaschool.classonapp.com/ajax/dictionary_search_word",
data: {searchWOrd: searchWOrd},
async: false,
success: function (response) {
if (response != 0)
{
res = JSON.parse(response);
if (res.word != '') {
$("#dictionary_search_word").html(res.word);
} else {
$("#dictionary_search_word").html('-');
}

if (res.partOfSpeech != '') {
$("#dictionary_partOfSpeech").html(res.partOfSpeech);
} else {
$("#dictionary_partOfSpeech").html('-');
}
if (res.definition != '') {
$("#dictionary_definition").html(res.definition);
} else {
$("#dictionary_definition").html('-');
}
if (res.example != '') {
$("#dictionary_example").html(res.example);
} else {
$("#dictionary_example").html('-');
}

if (res.synonyms != '') {
$("#dictionary_synonyms").html(res.synonyms);
} else {
$("#dictionary_synonyms").html('-');
}
$("#dictionary_word").css("border", "1px solid #F3F6F9");
$("#dictionary_result_div").show();
$("#dictionary_failure_div").hide();
} else {
$("#dictionary_result_div").hide();
$("#dictionary_failure_div").show();
}
}
});
}
});
});
</script>

<script>
$(".leave-icon").hide();
</script>
